#
# View "v_hadir"
#

DROP VIEW IF EXISTS `v_hadir`;
CREATE
  ALGORITHM = UNDEFINED
  VIEW `v_hadir`
  AS
  SELECT
    `orang`.`nama`, `hadir`.`tanggal`
  FROM
    (`orang`
      JOIN `hadir`)
  WHERE
    (`orang`.`id` = `hadir`.`id_orang`)
  GROUP BY
    `hadir`.`id`;

