# View "v_konfirmasi"
#

DROP VIEW IF EXISTS `v_konfirmasi`;
CREATE
  ALGORITHM = UNDEFINED
  VIEW `v_konfirmasi`
  AS
  SELECT
    `id`, `nama`, `konfirmasi2`
  FROM
    `orang`;
